mapper.update:

update ST_BOARD_MST
   set TTL       = #{ttl}
     , CNTS      = #{cnts}
     , EDIT_ID   = REG_ID
     , EDIT_DATE = date_format(SYSDATE(), '%Y%m%d%H%i%S')
     , FILE_YN   = (
                    select case when count(*) = 0 then 'N'
                                else 'Y'
                           end
                      from ST_BOARD_FILE
                     where MSEQ = #{SEQ}
                       and DEL_DATE is null
                   )
 where SEQ = #{SEQ};
